import { JWT_EXPIRY_TIME } from '../constants/config';
import { setRefreshToken, getRefreshToken } from '../storage/Cookie';
import API from '../api';
import * as authApi from '../api/auth';

export const refresh = async () => {
	const data = await authApi.refresh(getRefreshToken());

	login(data);
};

export const login = (refreshToken, accessToken) => {
	setRefreshToken(refreshToken);
	API.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`;

	setTimeout(refresh(), JWT_EXPIRY_TIME - 60 * 1000);
};
