import React, { useState, useEffect } from 'react';
/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';

import TopNavigation from '../../components/Common/TopNavigation/TopNavigation';
import BottomNavigation from '../../components/Common/BottomNavigation/BottomNavigation';
import { useNavigate } from 'react-router-dom';
import null_profile from '../../assets/null_profile_img.png';
import gallery_img from '../../assets/gallery_img.png';
import ProfileTop from '../../components/Profile/ProfileTop';
import ProfileButton from '../../components/Profile/ProfileButton';
import { useUserState } from '../../context/User/UserContext';
import { getUserInfo } from '../../api/user';

const ProfileUser = ({ articleId }) => {
	const { user_id } = useUserState();
	const navigate = useNavigate();
	const [profileInfo, setProfileInfo] = useState(null);
	useEffect(() => {
		const fetch = async () => {
			const data = await getUserInfo();
			setProfileInfo(data.userInfo);
			console.log(data);
		};
		fetch();
	}, []);
	return (
		<>
			{profileInfo && (
				<TopNavigation
					backClick
					onBackClick={() => navigate(-1)}
					leftContent={<span>{profileInfo.userId}</span>}
				/>
			)}

			<div
				className="wrapper"
				style={{
					width: '100%',
					height: '100%',
					overflowY: 'scroll',
				}}
			>
				{/* 프로필 상단 - 바이오, 버튼 */}
				<div>
					{/* 프로필 바이오 */}
					{profileInfo && (
						<ProfileTop
							key={profileInfo.userId}
							imgSrc={null_profile}
							followerCount={profileInfo.userFollowedCnt}
							followingCount={profileInfo.userFollowingCnt}
							clothesCount={profileInfo.userArticleCnt}
						/>
					)}
					{/* 버튼 */}
					{user_id !== profileInfo.userId && <ProfileButton />}
				</div>
				{/* 옷장 사진 */}
				<div
					css={css`
						display: flex;
						flex-direction: column;
					`}
				>
					{/* 사진 */}
					<div
						css={css`
							display: grid;
							grid-template-columns: 1fr 1fr;
							grid-template-rows: auto;
							place-items: center;
							gap: 10px;
							padding: 10px;
						`}
					>
						<img
							css={css`
								display: flex;
								width: 100%;
								height: 100%;
							`}
							src={gallery_img}
							alt=""
							onClick={() => navigate(`/article/${articleId}`)}
						/>
					</div>
				</div>
			</div>
			<BottomNavigation />
		</>
	);
};

export default ProfileUser;
