const StyledSlider = styled(Slider)`
	width: 100%;
	height: 100%;
	.slick-list {
		height: 100%;
		width: 100%;
	}
	.slick-dots {
		bottom: 0;
		li {
			width: 10px;
		}
		li > button::before {
			font-size: 30px;
			opacity: 0.25;
		}
		li.slick-active > button::before {
			opacity: 0.75;
		}
	}
	.slick-prev {
		left: 0;
		z-index: 1;
	}
	.slick-next {
		right: 0;
		z-index: 1;
	}
`;
const Carousel = ({ imgSrcList, style, contentHeight }) => {
	const settings = {
		arrows: false,
		dots: true,
		infinite: true,
		speed: 500,
		slidesToShow: 1,
		slidesToScroll: 1,
		// prevArrow: <PrevArrow />,
	};
	return (
		<div style={{ ...style }}>
			<StyledSlider {...settings}>
				{imgSrcList &&
					imgSrcList.map(src => (
						<div key={uuid()}>
							<div style={{ height: contentHeight }}>
								<img
									src={src}
									alt=""
									style={{
										width: '100%',
										height: '100%',
									}}
								/>
							</div>
						</div>
					))}
			</StyledSlider>
		</div>
	);
};

export default Carousel;
