/** @jsxImportSource @emotion/react */
import styled from 'styled-components';

export const RadioLabel = styled.label`
	position: absolute;
	width: 24px;
	height: 24px;
	border-radius: 50%;
	background: white;
	border: 1px solid #bebebe;
`;

export const OptionSelectBtn = styled.input`
	opacity: 0;
	z-index: 1;
	border-radius: 50%;
	margin-right: 10px;
	&:hover ~ ${RadioLabel} {
		background: #bebebe;
		&::after {
			content: url('../../../assets/checkbox2.svg');
			display: block;
			border-radius: 50%;
			width: 12px;
			height: 12px;
			margin: 5px;
			background: #ffffff;
		}
	}
	&:checked + ${RadioLabel} {
		background: #000000;
		border: 1px solid #000000;
		&::after {
			content: url('../../../assets/checkbox2.svg');
			display: block;
			border-radius: 50%;
			width: 12px;
			height: 12px;
			margin: 5px;
			box-shadow: 1px 3px 3px 1px rgba(0, 0, 0, 0.1);
			background: white;
		}
	}
`;
