import React, { useState, useEffect } from 'react';
/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import { useNavigate, useParams } from 'react-router-dom';

import colors from '../../constants/colors';
import TopNavigation from '../../components/Common/TopNavigation/TopNavigation';
import Button from '../../components/Common/Button/Button';
import Input from '../../components/Common/Input/Input';
import modify_img from '../../assets/modify_img.png';
import { modifyArticle, deleteArticle, getArticleDetail } from '../../api/article';
import TextArea from '../../components/Common/TextArea/TextArea';
import { Checkbox } from '../../components/Common/CheckBox/Checkbox';
import getByte from '../../utils/getByte';
import delete_icon from '../../assets/delete.svg';
import Modal from '../../components/Common/Modal/Modal';
import trash_modal_icon from '../../assets/trash_modal_Icon.svg';
import { MAX_BYTE } from '../../constants/config';
import { useRef } from 'react';
import Carousel from '../../components/Common/Carousel/Carousel';
import ArticleCategory from '../../components/Category/ArticleCategory';

const ArticleModify = () => {
	const { articleId } = useParams();
	const [activeCategory, setActiveCategory] = useState('etc');
	const [title, setTitle] = useState('');
	const [price, setPrice] = useState('');
	const [content, setContent] = useState('');
	const [isRent, setIsRent] = useState(false);
	const [modalVisible, setModalVisible] = useState(false);
	const [imageSrcs, setImageSrcs] = useState([]);
	const fileRef = useRef(null);
	const navigate = useNavigate();
	useEffect(() => {
		const fetch = async () => {
			const data = await getArticleDetail(articleId);
			console.log(data);
			setTitle(data.title);
			setPrice(data.price);
			setContent(data.content);
			setIsRent(data.lendStatus);
			setActiveCategory(data.category);
			setImageSrcs(data.feedImg.map(img => img.baseUrl + img.img));
		};
		fetch();
	}, []);

	const encodeFileToBase64 = fileBlob => {
		const reader = new FileReader();
		reader.readAsDataURL(fileBlob);
		return new Promise(resolve => {
			reader.onload = () => {
				setImageSrcs(prev => [...prev, reader.result]);
				resolve();
			};
		});
	};
	const fileInputChangeHandler = e => {
		if (e.target.files.length <= 5) {
			if (e.target.files.length > 0) {
				setImageSrcs([]);
				Array.from(e.target.files).forEach(file => encodeFileToBase64(file));
			}
		} else {
			alert('최대 5개만 업로드할 수 있습니다.');
		}
	};
	const openModal = () => {
		setModalVisible(true);
	};
	const closeModal = () => {
		setModalVisible(false);
	};
	const categoryClickHandler = async e => {
		setActiveCategory(e.target.id);
	};
	const onContentChangeHandler = e => {
		e.preventDefault();
		if (getByte(e.target.value) <= MAX_BYTE) setContent(e.target.value);
	};
	const onModifyClickHandler = async () => {
		if (imageSrcs.length === 0) {
			alert('사진을 등록해주세요');
			return;
		}
		const body = {
			title: title,
			price: price,
			content: content,
			lendstatus: isRent,
			photos: imageSrcs,
			category: activeCategory,
		};

		try {
			const ret = await modifyArticle(articleId, body);
			navigate(`/article/${articleId}`);
		} catch (error) {
			alert('다시 시도하세요.');
		}
	};
	const onDeleteClickHandler = async () => {
		try {
			const ret = await deleteArticle();
			navigate('/article/list');
		} catch (error) {
			alert('다시 시도하세요.');
		}
	};

	return (
		<>
			<TopNavigation
				backClick
				onBackClick={() => navigate(-1)}
				leftContent={<span>FITWEEN</span>}
				rightMenu={
					<img
						src={delete_icon}
						alt=""
						onClick={() => {
							openModal();
						}}
					/>
				}
			/>
			<div
				className="wrapper"
				style={{
					width: '100%',
					height: '100%',
					overflow: 'scroll',
				}}
			>
				{modalVisible && (
					<Modal visible={modalVisible} maskClosable onClose={openModal} type="center">
						<div
							css={css`
								display: flex;
								flex-direction: column;
								align-items: center;
								gap: 25px;
							`}
						>
							<img src={trash_modal_icon} alt="" />
							<div style={{ fontFamily: 'Medium', fontSize: 18 }}>
								해당 게시글을 삭제하시겠습니까?
							</div>
							<div
								css={css`
									display: flex;
									flex-direction: row;
									align-items: flex-start;
									padding: 0px;
									gap: 16px;
								`}
							>
								<button
									onClick={closeModal}
									css={css`
										width: 100px;
										height: 45px;
										background: #f1f1f1;
										border-radius: 50px;
										font-family: 'Medium';
										font-size: 16px;
										border: none;
									`}
								>
									취소
								</button>
								<button
									onClick={onDeleteClickHandler}
									css={css`
										width: 100px;
										height: 45px;
										background: #6cc4a1;
										border-radius: 50px;
										border: none;
										color: white;
										font-family: 'Medium';
										font-size: 16px;
									`}
								>
									삭제
								</button>
							</div>
						</div>
					</Modal>
				)}
				{/* 사진 등록 */}
				<form name="imageForm">
					<Carousel
						style={{ marginBottom: '30px' }}
						contentHeight="300px"
						imgSrcList={imageSrcs.length > 0 ? imageSrcs : [modify_img]}
						onClick={e => {
							fileRef.current.click();
						}}
					/>
					<input
						ref={fileRef}
						name="file"
						type="file"
						multiple
						accept="image/*"
						style={{ display: 'none' }}
						onChange={fileInputChangeHandler}
					/>
				</form>
				<div
					css={css`
						display: flex;
						flex-direction: column;
						padding: 0px 25px 30px 25px;
					`}
				>
					<div
						css={css`
							margin-bottom: 30px;
						`}
					>
						<ArticleCategory
							activeCategory={activeCategory}
							onClickHandler={categoryClickHandler}
						/>
					</div>
					{/* 제목 */}
					<Input
						css={css`
							margin-bottom: 12px;
							border: 1px solid ${colors.black};
							text-align: center;
							box-shadow: none;
						`}
						type="text"
						value={title}
						onChange={e => setTitle(e.target.value)}
						placeholder="제목"
					/>
					{/* 대여 가격 */}
					<Input
						css={css`
							margin-bottom: 12px;
							border: 1px solid ${colors.black};
							text-align: center;
							box-shadow: none;
						`}
						type="number"
						value={price}
						onChange={e => setPrice(e.target.value)}
						placeholder="1일 대여 가격"
					/>
					{/* 내용 */}
					<TextArea
						css={css`
							text-align: center;
							border: 1px solid ${colors.black};
							box-shadow: none;
						`}
						type="text"
						value={content}
						onChange={onContentChangeHandler}
						maxByte={MAX_BYTE}
						placeholder="내용"
					/>
					<div
						css={css`
							display: flex;
							align-items: center;
							justify-content: center;
						`}
					>
						<Checkbox
							checked={isRent}
							onChange={e => setIsRent(e.target.checked)}
							value={isRent}
							label={isRent ? '대여 가능' : '대여 불가'}
							id="checkrent"
						/>
					</div>
				</div>
				<div
					css={css`
						display: flex;
						padding: 0px 25px 25px 25px;
					`}
				>
					<Button
						onClick={onModifyClickHandler}
						type="active"
						label="수정하기"
						style={{ padding: 10 }}
					/>
				</div>
			</div>
		</>
	);
};

export default ArticleModify;
