export const onLoginSuccess = (refreshToken, accessToken) => {
	setRefreshToken(refreshToken);
	API.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`;

	// setTimeout(onRefresh(), JWT_EXPIRY_TIME - 60 * 1000);
};
