const userReducer = (state, action) => {
	const userId = JSON.parse(localStorage.getItem('loginedUserId'));

	switch (action.type) {
		case 'LOGIN':
			return {
				loginedUserId: userId ? userId : null,
			};
		case 'LOGOUT':
			return {
				loginedUserId: null,
			};
		case 'SIGNUP':
			return {
				loginedUserId: userId ? userId : null,
			};
		default:
			return state;
	}
};

export default userReducer;
